import React from 'react'
import ReactPlayer from 'react-player';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const YoutubeSlide = ({ url, isSelected } : { url: string; isSelected?: boolean }) => (
    <ReactPlayer width="100%" url={url} playing={isSelected} />
);



function parseXML(text) {
    parser = new DOMParser();
    if (window.DOMParser) {
        parser = new DOMParser();
        doc = parser.parseFromString(text,"text/xml");
    }
    else { // Internet Explorer
        doc = new ActiveXObject("Microsoft.XMLDOM");
        doc.async="false";
        doc.loadXML(text);
    }
    return doc;
}



 const CarouselVideo = () => {

    const customRenderItem = (item, props) => <item.type {...item.props} {...props} />;

    const getVideoThumb = (videoId) => {
        const xml = parseXML(`http://vimeo.com/api/v2/video/${videoId}.xml`)

        const thumbnail = xml.getElementsByTagName('thumbnail_medium')
       return thumbnail;

    };

    const getVideoId = (url) => url.substr('https://player.vimeo.com/video/'.length, url.length);

    const customRenderThumb = (children) =>
        children.map((item) => {
            const videoId = getVideoId(item.props.url);
            return <img src={getVideoThumb(videoId)} />;
        });

    return (

        <Carousel renderItem={customRenderItem} renderThumbs={customRenderThumb}>
            <YoutubeSlide key="youtube-1" url="https://player.vimeo.com/video/534212751" />
            <YoutubeSlide key="youtube-2" url="https://www.youtube.com/embed/mOdmi9SVeWY" />
            <YoutubeSlide key="youtube-3" url="https://www.youtube.com/embed/n0F6hSpxaFc" />
            <YoutubeSlide key="youtube-4" url="https://www.youtube.com/embed/0uGETVnkujA" />
        </Carousel>
    );
};

export default CarouselVideo
